import { Link } from "react-router-dom";
import React, { useState } from 'react';
import logo from '../logo_transparent_bg.png'; // Import the logo image correctly
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram } from '@fortawesome/free-brands-svg-icons';
import { faCartShopping, faBars } from '@fortawesome/free-solid-svg-icons'

function NavBar() {
    const [showMenu, setShowMenu] = useState(false);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
    };

    return (
        <>
            <nav className="bg-blackShade">
                <div className="flex flex-col md:flex-row md:justify-evenly justify-between items-center">
                    <img className="object-contain md:h-32 w-72 h-24 md:flex-3 flex-0 mb-4 md:mb-0" src={logo} alt="Logo" />
                    <button className="md:hidden text-2xl text-white mx-8 transition-all ease-in-out delay-100 duration-100 hover:text-stone" onClick={toggleMenu} aria-expanded={showMenu ? "true" : "false"}>
                        <FontAwesomeIcon icon={faBars}/>
                    </button>
                    <ul className={`md:flex flex-col md:flex-row justify-between items-center ${showMenu ? 'block' : 'hidden'}`}>
                        <li className="font-PT_Serif px-8 text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone mb-4 md:mb-0"><Link to="/">Home</Link></li>
                        <li className="font-PT_Serif px-8 text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone mb-4 md:mb-0">About</li>
                        <li className="font-PT_Serif px-8 text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone mb-4 md:mb-0"><Link to="/contact">Contact</Link></li>
                        <li className="font-PT_Serif px-8 text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone mb-4 md:mb-0"><Link to="/store">Store</Link></li>
                        <li className="text-2xl text-white mx-2 transition-all text-center ease-in-out delay-100 duration-100 hover:text-stone"><Link to="https://www.instagram.com/timelesskettlesuk/"><FontAwesomeIcon icon={faInstagram}/></Link></li>
                        <li className="text-2xl text-white mx-4 transition-all ease-in-out text-center delay-100 duration-100 hover:text-stone"><Link rel="icon" to="/cart"><FontAwesomeIcon icon={faCartShopping}/></Link></li>
                    </ul>
                </div>
            </nav>
        </>
    );
}

export default NavBar;
