import React, {useState} from 'react';
import axios from 'axios';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


export function AddToCartButton(props) {

const [isClicked, setIsClicked] = useState(false)

    const addToCart = () => {
        const { itemData } = props;
        const id = itemData.id;
        const itemQuantity = 1;
        const postData = async () => {
            try {
                const response = await axios.post(`http://localhost:4000/cart?productId=${id}&quantity=${itemQuantity}`, null, {
                    withCredentials: true // Include credentials in the request for security reasons
                });
                console.log(response);
                setIsClicked(true);
                console.log(isClicked)
            } catch (error) {
                console.error(error);
            }
        };
        postData();
    };



    return (
        <>
        <div className='flex flex-row items-center bg-gray-100 rounded p-1.5 border border-gray-300 drop-shadow-md hover:bg-blackShade hover:text-gray-200 transition-all'>
            <button onClick={addToCart} className='font-Lexend font-sm' type="button">Add to Cart</button>
            <FontAwesomeIcon className='px-2' icon={faShoppingCart}/>
        </div>
        </>
    );
}
