import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import { faInstagram } from '@fortawesome/free-brands-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { ProductCard } from '../components/productCard';
import axios from 'axios';

export function Store() {
    const [data, setData] = useState([]);
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { data: response } = await axios.get('http://localhost:4000/products');
                setData(response["1"]);
                setImages(response["2"]);
            } catch (error) {
                console.error(error);
            }
            setLoading(false);
        };
        fetchData();
    }, []);

    function ImageSort(id) {
        const result = images.find(obj => obj.id === id)?.image_location;
        return result; // Return the image location
    }

    return (
        <div className='my-36'>
            <section className="bg-white" id="about">
                <div className="py-8 px-2 mx-auto max-w-screen-xl lg:py-16">
                    <div className="bg-gray-100 border border-gray-200 rounded-lg p-8 md:p-12 mb-8 flex flex-col items-center">
                        <h1 className="text-blackShade text-3xl md:text-5xl font-extrabold font-EB_Garamond mb-2 drop-shadow-lg text-center hover:text-6xl transition-all">Timeless Kettles Store</h1>
                        <p className="text-lg text-center font-normal font-Lexend text-gray-600 mb-6 drop-shadow-xl">Browse thorough our collection of merchandise below:</p>
                        <a href="https://www.instagram.com/timelesskettlesuk/" className="w-full sm:w-auto bg-gray-800 hover:bg-gray-700 focus:ring-4 focus:outline-none focus:ring-gray-300 text-white rounded-lg inline-flex items-center justify-center px-4 py-2.5 ">
                            <FontAwesomeIcon className="text-center text-white md:text-5xl text-3xl py-2 px-4 block hover:text-stone transition-all" icon={faInstagram}/>
                            <div className="text-center rtl:text-right">
                                <div className="mb-1 text-xs">Follow us on</div>
                                <div className="mt-1 font-sans text-md font-bold">Instagram</div>
                                <div className='text-xs mt-1'>@timelesskettlesuk</div>
                            </div>
                        </a>
                    </div>
                </div>
            </section>
            <div className='grid grid-cols-3 gap-8 p-8'>
                {data.map((item, index) => (
                    <li key={index}>
                        <ProductCard itemName={item.name}/>
                        {/* Render the image location */}
                        <h1>{ImageSort(item.id)}</h1>
                    </li>
                ))}
            </div>
        </div>
    );
}
