import React from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from "@fortawesome/free-solid-svg-icons";
import { AddToCartButton } from "./addToCartButton";


export function ProductCard(props){

    return (
        <>
            <div class="w-full max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                <Link to={`/store/${props.itemData.id}`}>
                    <img class="p-8 rounded-t-lg object-contain h-56 w-96" src={`${process.env.PUBLIC_URL}/images/${props.functionToPass[0].image_location}`} alt="product image" />
                </Link>
                <div class="px-5 pb-5">
                    <Link to={`/store/${props.itemData.id}`}>
                        <h5 class="text-2xl font-Lexend tracking-tight text-gray-900">{props.itemName}</h5>
                    </Link>
                    <div class="flex items-center mt-2.5 mb-5">
                        <div class="flex flex-row items-center space-x-1 rtl:space-x-reverse">
                           <h5 className="font-Lexend font-xs font-bold">Colour: </h5>
                           <FontAwesomeIcon className={`text-${props.itemData.color}-400 text-xl`} icon={faCircle}/>
                        </div>
                    </div>
                    <div class="flex items-center justify-between">
                        <span class="text-3xl font-bold text-gray-900">Â£{props.itemData.price}</span>
                        <AddToCartButton itemData={props.itemData.id} />
        </div>
    </div>
    <div>
                {props.functionToPass.map((item, index) => (
                        <h1>{item.image_location}</h1>
                ))}
            </div>
</div>
        </>
    )

}
