import { Link } from "react-router-dom";
import logo from '../logo_transparent_bg.png'; // Import the logo image correctly
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram } from '@fortawesome/free-brands-svg-icons';
import { faCartShopping, faBars } from '@fortawesome/free-solid-svg-icons'

function NavBar() {
    const [showMenu, setShowMenu] = useState(false);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
    };

    return (
        <>
            <nav className="bg-blackShade">
                <ul className={`flex flex-row justify-between items-center ${showMenu ? 'block' : 'hidden'}`}>
                    <img className="object-contain md:h-32 w-72 h-24 md:flex-3 flex-0" src={logo} alt="Logo" />
                    <li className="font-PT_Serif text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/">Home</Link></li>
                    <li className="font-PT_Serif text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone">About</li>
                    <li className="font-PT_Serif text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/contact">Contact</Link></li>
                    <li className="font-PT_Serif text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/store">Store</Link></li>
                    <li className="text-2xl text-white mx-2 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="https://www.instagram.com/timelesskettlesuk/"><FontAwesomeIcon icon={faInstagram}/></Link></li>
                    <li className="text-2xl text-white mx-4 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link rel="icon" to="/cart"><FontAwesomeIcon icon={faCartShopping}/></Link></li>
                </ul>
                <button className="lg:invisible md:visible text-2xl text-white mx-12 transition-all ease-in-out delay-100 duration-100 hover:text-stone sm:block" onClick={toggleMenu} aria-expanded={showMenu ? "true" : "false"}>
                    <FontAwesomeIcon icon={faBars}/>
                </button>
            </nav>
        </>
    );
}

export default NavBar;
