import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export function Cart() {
    const [cartData, setData] = useState([null]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:4000/cart', {
                    withCredentials: true
                });
                setData(response.data.data);
                console.log(cartData);
            } catch (error) {
                console.error(error);
            }
            setLoading(false);
        };
        fetchData();
    }, [loading]);


    const checkout = () => {
        const postCart = async () => {
            try {
                const requestData = {
                    cartData: cartData,
                };
    
                const response = await axios.post(
                    'http://localhost:4000/create-checkout-session',
                    requestData,
                    {
                        withCredentials: true // Include credentials in the request
                    }
                );
                
                console.log(response.data.url);
                window.location.href = response.data.url;
            } catch (error) {
                console.error(error);
            }
        };
        postCart();
    }

    const removeItem = (item) => {
        const postDelete = async () => {
            try{
                const response = await axios.delete('http://localhost:4000/cart',
            item, {
                withCredentials: true
            }
        );
            } catch (error) {

            }
        }
    }

    return (
        <>
            <h1>Cart</h1>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <>
                    {cartData.length === 0 ? (
                        <p>Your basket is empty!</p>
                    ) : (
                        <ul>
                            {cartData.map((item, index) => (
                                <li key={index}>
                                    <p>Item: {item.itemname}</p>
                                    <p>Price: Â£{item.unit_price}</p> {/* Assuming you have price in your cart item object */}
                                    <p>Quantity: {item.quantity}</p>
                                    <button onClick={removeItem} item={item}>Delete</button>
                                </li>
                            ))}
                                <button onClick={checkout}>Checkout</button>

                        </ul>
                        
                    )}
                </>
            )}
        </>
    );
    
}
