import { Link } from "react-router-dom";
import logo from '../logo_transparent_bg.png'; // Import the logo image correctly
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram } from '@fortawesome/free-brands-svg-icons';
import { faCartShopping, faBars } from '@fortawesome/free-solid-svg-icons'

function NavBar() {
    return (
        <>
        <nav className="bg-blackShade">
            <div>
            <button className="lg:invisible md:visible text-2xl text-white mx-12 transition-all ease-in-out delay-100 duration-100 hover:text-stone sm:block" data-collapse-toggle="navbar-sm" type="button" aria-controls="navbar-sm" aria-expanded="false">
                        <FontAwesomeIcon icon={faBars}/>
                    </button>
                
            </div>
            <ul className="flex flex-row justify-between items-center">
                {/* Use the imported logo image */}
                <img className="object-contain md:h-32 w-72 h-24 md:flex-3 flex-0" src={logo} alt="Logo" />
               
                
                <li className="lg:block md:hidden hidden font-PT_Serif text-2xl text-white text-center flex-1 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/">Home</Link></li>
                <li className="lg:block md:hidden hidden font-PT_Serif text-2xl text-white text-center flex-1 transition-all ease-in-out delay-100 duration-100 hover:text-stone">About</li>
                <li className="lg:block md:hidden hidden font-PT_Serif text-2xl text-white text-center flex-1 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/contact">Contact</Link></li>
                <li className="lg:block md:hidden hidden font-PT_Serif text-2xl text-white text-center flex-1 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/store">Store</Link></li>
                <li className="lg:block md:hidden hidden text-2xl text-white mx-2 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="https://www.instagram.com/timelesskettlesuk/"><FontAwesomeIcon icon={faInstagram}/></Link></li>
                <li className="lg:block md:hidden hidden text-2xl text-white mx-4 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link rel="icon" to="/cart"><FontAwesomeIcon icon={faCartShopping}/></Link></li>
               

             </ul>
             
        </nav>        
        </>
    );
}

export default NavBar;
