import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

export function Cart() {
    const [cartData, setData] = useState([null]);
    const [loading, setLoading] = useState(true);
    const [data, setCartData] = useState([]);
    const [images, setImages] = useState([])

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:4000/cart', {
                    withCredentials: true
                });
                setData(response.data.data);
                console.log(cartData);
                const { data: responseItems} = await axios.get('http://localhost:4000/products');
                setCartData(responseItems["1"]);
                setImages(responseItems["2"]);
            } catch (error) {
                console.error(error);
            }
            setLoading(false);
        };
        fetchData();
    }, [loading]);


    const checkout = () => {
        const postCart = async () => {
            try {
                const requestData = {
                    cartData: cartData,
                };
    
                const response = await axios.post(
                    'http://localhost:4000/create-checkout-session',
                    requestData,
                    {
                        withCredentials: true // Include credentials in the request
                    }
                );
                
                console.log(response.data.url);
                window.location.href = response.data.url;
            } catch (error) {
                console.error(error);
            }
        };
        postCart();
    }

    const removeItem = (item) => {
        const postDelete = async () => {
            try{
                const response = await axios.post('http://localhost:4000/cart/removeFromCart',
            item, {
                withCredentials: true
            }
            
        );
        setLoading(true)
        console.log(response)
            } catch (error) {

            }
        }
        postDelete()
    }

console.log(images)

const findImages = (id) => {
    if (!loading) {
        const filteredImages = images.filter(image => image.product_id === id);
        if(filteredImages.length > 0){
            console.log(filteredImages)
            return <img 
            class="p-8 rounded-t-lg object-contain h-56 w-96" 
            src={`${process.env.PUBLIC_URL}/images/${filteredImages[0].image_location}`} 
            alt="product image" 
            />

        }

    }

};

if(loading){

} else {
    return (
        <>
            <div className='mt-48'>
                <div className='grid grid-cols-3 gap-4 py-8 px-1 mx-auto max-w-screen-xl lg:py-1'>
                    <div className='md:col-span-2 col-span-1'>
                        <div className='flex flex-row'>
                            <h1 className='font-Lexend font-bold text-3xl mr-4'>Your cart: </h1>
                            {cartData.length === 0 ? (
                                <h1 className='font-Lexend font-bold text-3xl'>No Items</h1>
                            ) : (
                                <h1 className='font-Lexend font-bold text-3xl'> {cartData.length} Items</h1>
                 
                            )}
                        </div>
                        <div className="flex flex-row mt-10 justify-center border rounded-lg drop-shadow-md">
                            <h1 className="p-4 font-Lexend font-semibold"><span className='font-bold text-gray-500'>Free UK delivery</span> &nbsp;on all orders!</h1>
                        </div>
                        <ul>
                        {cartData.map((item, index) => (
                            <li key={index}>
                                 <div className='flex flex-row'>
                                   <div className='p-6 border-r-4 mt-4'>
                                     {findImages(item.product_id)}
                                   </div>
                                   <div className='flex flex-col p-6'>
                                       <h1 className='font-Lexend font-bold text-2xl'>{item.itemname}</h1>
                                       <h1 className='mt-2 font-Lexend text-sm text-gray-600'>{item.product_id}</h1>
                                       <h1 className='mt-2 font-Lexend font-bold text-xl text-gray-600'>£{item.unit_price}</h1>
                                       <div className='flex flex-row items-center'>
                                            <form class="max-w-sm mx-auto mt-8">
                                                <label for="quantity" class="block mb-2 text-sm font-medium font-Lexend text-gray-900">Item quantity:</label>
                                                <select id="quantity" class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg block w-full p-2.5">
                                                    <option selected>{item.quantity}</option>
                                                    <option value="1">1</option>
                                                    <option value="1">2</option>
                                                    <option value="1">3</option>
                                                    <option value="1">4</option>
                                                    <option value="1">5</option>
                                                </select>
                                            </form>
                                            <button type="button" className="py-2 ml-12 font-Lexend hover:text-red-600 text-md transition-all mt-10" onClick={() => removeItem(item)}>
                                                <FontAwesomeIcon icon={faTrash} />
                                            </button>
                                       </div>


                            
                                   </div>
                               </div>
                            </li>
                               
                        ))}
                        </ul>

                    </div>
    
                </div>
            
            <h1>Cart</h1>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <>
                    {cartData.length === 0 ? (
                        <p>Your basket is empty!</p>
                    ) : (
                        <ul>
                            {cartData.map((item, index) => (
                                <li key={index}>
                                    <p>Item: {item.itemname}</p>
                                    <p>Price: £{item.unit_price}</p> {/* Assuming you have price in your cart item object */}
                                    <p>Quantity: {item.quantity}</p>
                                    <button onClick={() => removeItem(item)}>Delete</button>
                                </li>
                            ))}
                                <button onClick={checkout}>Checkout</button>

                        </ul>
                        
                    )}
                
                </>
            )}
            </div>
        </> 
    );
    
}

    
}
