import { useParams } from "react-router-dom";
import React, { useEffect, useState } from 'react';
import { AddToCartButton } from "../components/addToCartButton";
import axios from 'axios';

export function Item() {
    const { id } = useParams();
    const itemQuantity = 1;
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { data: response } = await axios.get(`http://localhost:4000/products/${id}`, {
                    withCredentials: true //SECURITY CHECK? Include credentials in the request
                });
                setData(response["1"]);
            } catch (error) {
                console.error(error);
            }
            setLoading(false);
        };
        fetchData();
    }, [id]);



    if (loading) {
        return <p>Loading...</p>;
    }

    if (!data) {
        return <p>No data found.</p>;
    }



    return (
        <div className='mt-48'>
            
            <h1>{data[0].name}</h1>
            <h2>Â£{data[0].price}</h2>
            <ul>
                <li>{data[0].category}</li>
            </ul>
            <p>{data[0].description}</p>
            <AddToCartButton itemData={data}/>
        </div>
    );
}
