import axios from 'axios';

export function AddToCartButton(){

    const addToCart = (props) => {
        alert(props.itemData.id)
        const id = props.itemData.id;
        const itemQuantity = 1
        const postData = async () => {
            try{
                const response  = await axios.post(`http://localhost:4000/cart?productId=${id}&quantity=${itemQuantity}`, null, {
                    withCredentials: true //SECURITY CHECK? Include credentials in the request
                });
                console.log(response)
            } catch (error){
                console.error(error)
            }
        };
        postData();
    
    }

    return (
        <>
            <button onClick={addToCart}>Add</button>
        </>
    )


}
