import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import { faInstagram } from '@fortawesome/free-brands-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { ProductCard } from '../components/productCard';
import axios from 'axios';

export function Store() {
    const [data, setData] = useState([]);
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { data: response } = await axios.get('http://localhost:4000/products');
                setData(response["1"]);
                setImages(response["2"]);
            } catch (error) {
                console.error(error);
            }
            setLoading(false);
        };
       
        fetchData();
    }, []);

   
    const findImages = (id) => {
        if (!loading) {
            const filteredImages = images.filter(image => image.product_id === id);
            if(filteredImages.length > 0){
                console.log(filteredImages)
                return filteredImages
            }

            
        }

    };

    if(!loading){
        return (
            <div className='mt-36'>
                <section className="bg-white" id="about">
                    <div className="py-8 px-1 mx-auto max-w-screen-xl lg:py-16">
                        <div className="bg-gray-100 border border-gray-200 rounded-lg p-8 md:p-12 mb-8 flex flex-col items-center">
                            <h1 className="text-blackShade text-3xl md:text-5xl font-extrabold font-EB_Garamond mb-2 drop-shadow-lg text-center hover:text-6xl transition-all">Timeless Kettles Store</h1>
                            <p className="text-lg text-center font-normal font-Lexend text-gray-600 mb-6 drop-shadow-xl">Browse thorough our collection of merchandise below:</p>
                            <a target="_blank" href="https://www.instagram.com/timelesskettlesuk/" className="w-full sm:w-auto bg-gray-800 hover:bg-gray-700 focus:ring-4 focus:outline-none focus:ring-gray-300 text-white rounded-lg inline-flex items-center justify-center px-4 py-2.5 ">
                                <FontAwesomeIcon className="text-center text-white md:text-3xl text-1xl py-1 px-2 block hover:text-stone transition-all" icon={faInstagram}/>
                                <div className="text-center rtl:text-right">
                                    <div className="text-xs">Follow us on</div>
                                    <div className="font-sans text-sm font-bold">Instagram</div>
                                    <div className='text-xs'>@timelesskettlesuk</div>
                                </div>
                            </a>
                        </div>
                    </div>
                </section>
                <div className='grid lg:grid-cols-3 md:grid-cols-2 sm:grid-cols-1 place-content-center gap-8 py-8 px-2 mx-auto max-w-screen-xl lg:py-16'>
                    {data.map((item, index) => (
                            <ProductCard key={index} itemName={item.name} itemData={item} functionToPass={findImages(item.id)}/>
                    ))}
                </div>
            </div>
        );
    }
    else{
        return(
            <div className='my-36'>
                <div role="status" class="animate-pulse">
                    <div class="h-2.5 bg-gray-300 rounded-full dark:bg-gray-700 max-w-[640px] mb-2.5 mx-auto"></div>
                    <div class="h-2.5 mx-auto bg-gray-300 rounded-full dark:bg-gray-700 max-w-[540px]"></div>
                    <div class="flex items-center justify-center mt-4">
                        <svg class="w-8 h-8 text-gray-200 dark:text-gray-700 me-4" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
                            <path d="M10 0a10 10 0 1 0 10 10A10.011 10.011 0 0 0 10 0Zm0 5a3 3 0 1 1 0 6 3 3 0 0 1 0-6Zm0 13a8.949 8.949 0 0 1-4.951-1.488A3.987 3.987 0 0 1 9 13h2a3.987 3.987 0 0 1 3.951 3.512A8.949 8.949 0 0 1 10 18Z"/>
                        </svg>
                    <div class="w-20 h-2.5 bg-gray-200 rounded-full dark:bg-gray-700 me-3"></div>
                    <div class="w-24 h-2 bg-gray-200 rounded-full dark:bg-gray-700"></div>
                </div>
            <span class="sr-only">Loading...</span>
            </div>
            <div className="py-8 px-2 mx-auto max-w-screen-xl lg:py-16">
                <div role="status" class="max-w-sm p-4 border border-gray-200 rounded shadow animate-pulse md:p-6 dark:border-gray-700">
    <div class="flex items-center justify-center h-48 mb-4 bg-gray-300 rounded dark:bg-gray-700">
        <svg class="w-10 h-10 text-gray-200 dark:text-gray-600" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 16 20">
            <path d="M14.066 0H7v5a2 2 0 0 1-2 2H0v11a1.97 1.97 0 0 0 1.934 2h12.132A1.97 1.97 0 0 0 16 18V2a1.97 1.97 0 0 0-1.934-2ZM10.5 6a1.5 1.5 0 1 1 0 2.999A1.5 1.5 0 0 1 10.5 6Zm2.221 10.515a1 1 0 0 1-.858.485h-8a1 1 0 0 1-.9-1.43L5.6 10.039a.978.978 0 0 1 .936-.57 1 1 0 0 1 .9.632l1.181 2.981.541-1a.945.945 0 0 1 .883-.522 1 1 0 0 1 .879.529l1.832 3.438a1 1 0 0 1-.031.988Z"/>
            <path d="M5 5V.13a2.96 2.96 0 0 0-1.293.749L.879 3.707A2.98 2.98 0 0 0 .13 5H5Z"/>
        </svg>
    </div>
    <div class="h-2.5 bg-gray-200 rounded-full dark:bg-gray-700 w-48 mb-4"></div>
    <div class="h-2 bg-gray-200 rounded-full dark:bg-gray-700 mb-2.5"></div>
    <div class="h-2 bg-gray-200 rounded-full dark:bg-gray-700 mb-2.5"></div>
    <div class="h-2 bg-gray-200 rounded-full dark:bg-gray-700"></div>
    <div class="flex items-center mt-4">
       <svg class="w-10 h-10 me-3 text-gray-200 dark:text-gray-700" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
            <path d="M10 0a10 10 0 1 0 10 10A10.011 10.011 0 0 0 10 0Zm0 5a3 3 0 1 1 0 6 3 3 0 0 1 0-6Zm0 13a8.949 8.949 0 0 1-4.951-1.488A3.987 3.987 0 0 1 9 13h2a3.987 3.987 0 0 1 3.951 3.512A8.949 8.949 0 0 1 10 18Z"/>
        </svg>
        <div>
            <div class="h-2.5 bg-gray-200 rounded-full dark:bg-gray-700 w-32 mb-2"></div>
            <div class="w-48 h-2 bg-gray-200 rounded-full dark:bg-gray-700"></div>
        </div>
    </div>
    <span class="sr-only">Loading...</span>
</div>
</div>
            </div>
        )
    }

    
}
