import { Link } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export function Cart() {
    const [cartData, setData] = useState([null]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:4000/cart', {
                    withCredentials: true
                });
                setData(response.data.data);
                console.log(cartData);
            } catch (error) {
                console.error(error);
            }
            setLoading(false);
        };
        fetchData();
    }, [loading]);


    const checkout = () => {
        const postCart = async () => {
            try{
                const response  = await axios.post(`http://localhost:4000/create-checkout-session`, null, {
                    withCredentials: true, //SECURITY CHECK? Include credentials in the request
                    data: 'test'
                });
                console.log(response)
            } catch (error){
                console.error(error)
            }
        };
        postCart();
    
    }

    return (
        <>
            <h1>Cart</h1>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <>
                    {cartData.length === 0 ? (
                        <p>Your basket is empty!</p>
                    ) : (
                        <ul>
                            {cartData.map((item, index) => (
                                <li key={index}>
                                    <p>Item: {item.itemname}</p>
                                    <p>Price: {item.price}</p> {/* Assuming you have price in your cart item object */}
                                    <p>Quantity: {item.quantity}</p>
                                    <button onClick={checkout}>Checkout</button>
                                </li>
                            ))}
                        </ul>
                    )}
                </>
            )}
        </>
    );
    
}
