import { useParams } from "react-router-dom";
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export function Item() {
    const { id } = useParams();
    const itemQuantity = 1;
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { data: response } = await axios.get(`http://localhost:4000/products/${id}`, {
                    withCredentials: true //SECURITY CHECK? Include credentials in the request
                });
                setData(response["1"]);
            } catch (error) {
                console.error(error);
            }
            setLoading(false);
        };
        fetchData();
    }, [id]);



    if (loading) {
        return <p>Loading...</p>;
    }

    if (!data) {
        return <p>No data found.</p>;
    }

    const addToCart = () => {
        alert('Item added!')
        const postData = async () => {
            try{
                const response  = await axios.post(`http://localhost:4000/cart?productId=${id}&quantity=${itemQuantity}`, null, {
                    withCredentials: true //SECURITY CHECK? Include credentials in the request
                });
                console.log(response)
            } catch (error){
                console.error(error)
            }
        };
        postData();
    
    }

    return (
        <div className='mt-48'>
            <h1>{data[0].name}</h1>
            <h2>Â£{data[0].price}</h2>
            <ul>
                <li>{data[0].category}</li>
            </ul>
            <p>{data[0].description}</p>
            <button onClick={addToCart}>Add</button>
        </div>
    );
}
