import { Link } from "react-router-dom";
import logo from '../logo_transparent_bg.png'; // Import the logo image correctly
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram } from '@fortawesome/free-brands-svg-icons';
import { faCartShopping, faBars } from '@fortawesome/free-solid-svg-icons'

function NavBar() {
    return (
        <>
        <nav className="bg-blackShade">
            <div>
            </div>
            <ul className="flex flex-row justify-center items-center">
                {/* Use the imported logo image */}
                <img className="object-contain h-32 w-72 flex-3" src={logo} alt="Logo" />
                <li className="sm:invisible md:visible font-PT_Serif text-2xl text-white text-center flex-1 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/">Home</Link></li>
                <li className="md:invisible font-PT_Serif text-2xl text-white text-center flex-1 transition-all ease-in-out delay-100 duration-100 hover:text-stone">About</li>
                <li className="md:invisible font-PT_Serif text-2xl text-white text-center flex-1 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/contact">Contact</Link></li>
                <li className="md:invisible font-PT_Serif text-2xl text-white text-center flex-1 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="/store">Store</Link></li>
                <li className="text-2xl text-white mx-2 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link to="https://www.instagram.com/timelesskettlesuk/"><FontAwesomeIcon icon={faInstagram}/></Link></li>
                <li className="text-2xl text-white mx-4 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link rel="icon" to="/cart"><FontAwesomeIcon icon={faCartShopping}/></Link></li>
                <li className="md:invisible text-2xl text-white mx-4 transition-all ease-in-out delay-100 duration-100 hover:text-stone"><Link rel="icon" to="/cart"><FontAwesomeIcon icon={faBars}/></Link></li>

             </ul>
        </nav>        
        </>
    );
}

export default NavBar;
