import { useParams } from "react-router-dom";
import React, { useEffect, useState } from 'react';
import { AddToCartButton } from "../components/addToCartButton";
import axios from 'axios';

export function Item() {
    const { id } = useParams();
    const itemQuantity = 1;
    const [data, setData] = useState(null);
    const [images, setImages] = useState([]);
    const [currentImage, setCurrentImage] = useState()
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const { data: response } = await axios.get(`http://localhost:4000/products/${id}`, {
                    withCredentials: true //SECURITY CHECK? Include credentials in the request
                });
                setData(response["1"]);
                setImages(response["2"])
                setCurrentImage(response["2"][0].image_location)
            } catch (error) {
                console.error(error);
            }
            setLoading(false);
        };
        fetchData();
             
    }, [id]);
    

        if (!loading) {
            const filteredImages = images.filter(image => image.product_id === id);
            if(filteredImages.length > 0){
                console.log(filteredImages)
                return filteredImages
            }

        }

    console.log(currentImage)
  
    const handleImageClick = (imageSource) => {
        console.log("Image source:", imageSource);
        setCurrentImage(imageSource)
    }


    if (loading) {
        return (
            <>
                <div className='mt-48'>
                    <div className="grid md:grid-cols-2 md:grid-rows-4 grid-cols-1 grid-rows-5 gap-4">
                        <div className="md:row-span-3 row-span-2">
                            <div class="flex items-center justify-center h-full bg-gray-300 rounded sm:w-96">
                                <svg class="w-10 h-10 text-gray-200" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 18">
                                    <path d="M18 0H2a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h16a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2Zm-5.5 4a1.5 1.5 0 1 1 0 3 1.5 1.5 0 0 1 0-3Zm4.376 10.481A1 1 0 0 1 16 15H4a1 1 0 0 1-.895-1.447l3.5-7A1 1 0 0 1 7.468 6a.965.965 0 0 1 .9.5l2.775 4.757 1.546-1.887a1 1 0 0 1 1.618.1l2.541 4a1 1 0 0 1 .028 1.011Z"/>
                                </svg>
                            </div>
                        <div className="flex flex-row justify-start overflow-x-auto">
                            {images.map((item, index) => (
                                <img class="rounded-t-lg object-contain h-24 w-96" 
                                src={`${process.env.PUBLIC_URL}/images/${item.image_location}`} 
                                alt="product image"
                                onClick={() => handleImageClick(`${item.image_location}`)} />
                            ))}
                        </div>
                    </div>
                <div className="md:row-span-2 row-start-3">Heading</div>
                <div className="md:col-start-2 md:row-start-3 row-start-4">add to cart etc</div>
                <div className="md:col-span-2 md:row-start-4 row-start-5">'small print'</div>
            </div>
            </div>
            </>
        )
    } else {
        return (
            <>
            <div className='mt-48'>
                <div className="grid md:grid-cols-2 md:grid-rows-4 grid-cols-1 grid-rows-5 gap-4">
                    <div className="md:row-span-3 row-span-2">
                        <div className="flex justify-center">
                            <img class="rounded-t-lg object-contain h-full p-6" src={`${process.env.PUBLIC_URL}/images/${currentImage}`} alt="product image" />
                        </div>
                        <div className="flex flex-row justify-start overflow-x-auto">
                            {images.map((item, index) => (
                                <img class="rounded-t-lg object-contain h-24 w-96" 
                                src={`${process.env.PUBLIC_URL}/images/${item.image_location}`} 
                                alt="product image"
                                onClick={() => handleImageClick(`${item.image_location}`)} />
                            ))}
                        </div>
                    </div>
                <div className="md:row-span-2 row-start-3">Heading</div>
                <div className="md:col-start-2 md:row-start-3 row-start-4">add to cart etc</div>
                <div className="md:col-span-2 md:row-start-4 row-start-5">'small print'</div>
            </div>
            </div>
            </>
        );
    }



}
