import React, {useState} from 'react';
import axios from 'axios';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


export function AddToCartButton(props) {

    const [cartStatus, setCartStatus] = useState('Add to cart')
    const [cartClass, setCartClass] = useState('Add to cart')

    const addToCart = () => {
        const { itemData } = props;
        console.log(itemData)
        const id = itemData.id;
        const itemQuantity = 1;
        const postData = async () => {
            try {
                const response = await axios.post(`http://localhost:4000/cart?productId=${id}&quantity=${itemQuantity}`, null, {
                    withCredentials: true // Include credentials in the request for security reasons
                });
                console.log(response);
                setCartStatus('Item added!')
            } catch (error) {
                console.error(error);
            }
        };
        postData();
    };



    return (
        <div onClick={addToCart} className='cursor-pointer flex flex-row items-center bg-gray-100 rounded p-1.5 border border-gray-300 drop-shadow-md hover:bg-blackShade hover:text-gray-200 transition-all'>
            <button className='font-Lexend font-sm' type="button">{cartStatus}</button>
            <FontAwesomeIcon className='px-2' icon={faShoppingCart}/>
        </div>
    );
    
}
