import { Link } from "react-router-dom";
import React, { useState } from 'react';
import logo from '../logo_transparent_bg.png'; // Import the logo image correctly
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram } from '@fortawesome/free-brands-svg-icons';
import { faCartShopping, faBars } from '@fortawesome/free-solid-svg-icons'

function NavBar() {
    const [showMenu, setShowMenu] = useState(false);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
    };

    return (
        <nav className="bg-blackShade">
            <div className="flex justify-between items-center md:grid md:grid-cols-6">
                <div className="md:col-span-2 flex items-center">
                    <img className="object-contain md:h-32 w-72 h-24 flex-0" src={logo} alt="Logo" />
                    <button className="md:hidden text-2xl text-white mx-4 transition-all ease-in-out delay-100 duration-100 hover:text-stone" onClick={toggleMenu} aria-expanded={showMenu ? "true" : "false"}>
                        <FontAwesomeIcon icon={faBars}/>
                    </button>
                </div>
                <ul className={`md:flex md:col-span-4 md:justify-end md:gap-4 md:items-center ${showMenu ? 'block' : 'hidden'}`}>
                    <li className="font-PT_Serif px-8 text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone mb-4 md:mb-0"><Link to="/">Home</Link></li>
                    <li className="font-PT_Serif px-8 text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone mb-4 md:mb-0">About</li>
                    <li className="font-PT_Serif px-8 text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone mb-4 md:mb-0"><Link to="/contact">Contact</Link></li>
                    <li className="font-PT_Serif px-8 text-2xl text-white text-center transition-all ease-in-out delay-100 duration-100 hover:text-stone mb-4 md:mb-0"><Link to="/store">Store</Link></li>
                </ul>
                <div className="md:flex md:col-span-2 md:justify-end hidden">
                    <Link className="text-white mx-2 text-xl" to="https://www.instagram.com/timelesskettlesuk/"><FontAwesomeIcon icon={faInstagram}/></Link>
                    <Link className="text-white mx-4 text-xl" rel="icon" to="/cart"><FontAwesomeIcon icon={faCartShopping}/></Link>
                </div>
            </div>
        </nav>
    );
}

export default NavBar;
